                    carouselLeftNav =  element.find('.vmware-product-carosuel-leftNavigation'),
                    carouselPagination = element.find('.product-carosuel-pagination-container'),
                    
                    /* Navigation Button configuration */
                    NAV_BUTTON_WIDTH = 30,
                    MARGIN_LEFT = 35,
